import React, { useEffect, useState } from "react";
import { getPlants, deletePlant } from "../api/plantAPI";
import { Link } from "react-router-dom";

export default function Dashboard() {
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => { loadPlants(); }, []);

  async function loadPlants() {
    try {
      setLoading(true);
      const res = await getPlants();            // ‚¨ÖÔ∏è was getAllPlants
      setPlants(res.plants || []);
    } catch (err) {
      console.error("Error loading plants:", err);
      alert("Failed to load plants");
    } finally {
      setLoading(false);
    }
  }

  async function handleDelete(id) {
    if (!window.confirm("Delete this plant?")) return;
    try {
      await deletePlant(id);
      loadPlants();
    } catch (err) {
      console.error("Delete failed:", err);
      alert("Delete failed");
    }
  }

  return (
    <div className="page pb-24">
      <h1 className="h1 mb-4">üå± My Plants</h1>

      {loading && <p className="text-gray-500">Loading‚Ä¶</p>}

      {!loading && plants.length === 0 && (
        <p className="text-gray-600">No plants yet. Add some!</p>
      )}

      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {plants.map((p) => (
          <div key={p.id} className="card relative">
            {p.image_url && (
              <img
                src={p.image_url}
                alt={p.name || "plant"}
                className="w-full h-40 object-cover rounded-lg mb-2"
              />
            )}
            <h2 className="h2">{p.name || "Unnamed Plant"}</h2>
            {p.scientific_name && (
              <p className="text-sm text-gray-500 italic">
                {p.scientific_name}
              </p>
            )}

            <div className="mt-2 flex flex-wrap gap-3 text-sm text-gray-600">
              {p.sunlight && <span>‚òÄ {p.sunlight}</span>}
              {p.watering && <span>üíß {p.watering}</span>}
              {p.soil && <span>ü™® {p.soil}</span>}
            </div>

            <div className="flex justify-between items-center mt-4">
              <Link
                to={`/plant/${p.id}`}
                className="text-green-700 hover:underline text-sm"
              >
                View
              </Link>
              <button
                onClick={() => handleDelete(p.id)}
                className="text-red-600 hover:underline text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
