// src/components/BottomNav.jsx
import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FaHome, FaPlus, FaSearch, FaInfoCircle } from "react-icons/fa";

export default function BottomNav() {
  const { pathname } = useLocation();
  const active = (p) => (pathname === p ? "text-green-700" : "text-gray-500");

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-white border-t py-2">
      <div className="max-w-3xl mx-auto flex justify-around text-xl">
        <Link to="/dashboard" className={active("/dashboard")}><FaHome /></Link>
        <Link to="/new" className={active("/new")}><FaPlus /></Link>
        <Link to="/search" className={active("/search")}><FaSearch /></Link>
        <Link to="/about" className={active("/about")}><FaInfoCircle /></Link>
      </div>
    </nav>
  );
}
