import React, { useState } from "react";
import { searchPlantByName } from "../api/plantAPI";

export default function Search() {
  const [query, setQuery] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  async function handleSearch(e) {
    e.preventDefault();
    if (!query.trim()) return;
    setLoading(true);
    setResult(null);
    try {
      const res = await searchPlantByName(query.trim());
      setResult(res);
    } catch (err) {
      console.error("Search failed:", err);
      alert("Search failed");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="page pb-24">
      <h1 className="h1 mb-4">üîç Search Plants</h1>

      <form onSubmit={handleSearch} className="flex gap-2 mb-4">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter plant name (e.g., Rose)"
          className="flex-1 border rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-400"
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded-xl hover:bg-green-700"
        >
          Search
        </button>
      </form>

      {loading && <p className="text-gray-500">Searching...</p>}

      {result && (
        <div className="card">
          {result.imageUrl && (
            <img
              src={result.imageUrl}
              alt={result.name}
              className="w-full h-56 object-cover rounded-lg mb-3"
            />
          )}
          <h2 className="h2 capitalize">{result.name}</h2>
          <p className="text-sm text-gray-500">
            {result.source === "cache" ? "From DB cache" : "AI generated"}
          </p>
        </div>
      )}
    </div>
  );
}
