import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FaHome, FaPlus, FaSearch, FaInfoCircle } from "react-icons/fa";

export default function BottomNav() {
  const { pathname } = useLocation();
  const active = (p) =>
    pathname === p ? "text-green-700" : "text-gray-500 hover:text-green-700";

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur border-t py-2 z-20">
      <div className="max-w-3xl mx-auto flex justify-around text-xl">
        <Link to="/dashboard" className={`${active("/dashboard")} p-2 rounded-lg`}>
          <FaHome />
        </Link>
        <Link to="/new" className={`${active("/new")} p-2 rounded-lg`}>
          <FaPlus />
        </Link>
        <Link to="/search" className={`${active("/search")} p-2 rounded-lg`}>
          <FaSearch />
        </Link>
        <Link to="/about" className={`${active("/about")} p-2 rounded-lg`}>
          <FaInfoCircle />
        </Link>
      </div>
    </nav>
  );
}
