import React, { useEffect, useState } from "react";
import { getAllPlants, deletePlant } from "../api/plantAPI";
import { Link } from "react-router-dom";

export default function Dashboard() {
  const [plants, setPlants] = useState([]);

  useEffect(() => {
    loadPlants();
  }, []);

  async function loadPlants() {
    try {
      const res = await getAllPlants();
      setPlants(res.plants || []);
    } catch (err) {
      console.error("Error loading plants:", err);
    }
  }

  async function handleDelete(id) {
    if (!window.confirm("Are you sure you want to delete this plant?")) return;
    try {
      await deletePlant(id);
      loadPlants();
    } catch (err) {
      console.error("Delete failed:", err);
    }
  }

  return (
    <div className="page pb-24">
      <h1 className="h1 mb-4">ðŸŒ± My Plants</h1>

      {plants.length === 0 && (
        <p className="text-gray-600">No plants yet. Add some!</p>
      )}

      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {plants.map((p) => (
          <div key={p.id} className="card relative">
            {p.image_url && (
              <img
                src={p.image_url}
                alt={p.name}
                className="w-full h-40 object-cover rounded-lg mb-2"
              />
            )}
            <h2 className="h2">{p.name}</h2>
            {p.scientific_name && (
              <p className="text-sm text-gray-500 italic">{p.scientific_name}</p>
            )}

            <div className="mt-2 flex gap-2 text-sm text-gray-600">
              {p.sunlight && <span>â˜€ {p.sunlight}</span>}
              {p.watering && <span>ðŸ’§ {p.watering}</span>}
            </div>

            <div className="flex justify-between items-center mt-3">
              <Link
                to={`/plant/${p.id}`}
                className="text-green-700 hover:underline text-sm"
              >
                View
              </Link>
              <button
                onClick={() => handleDelete(p.id)}
                className="text-red-600 hover:underline text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
