import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function Header() {
  const { pathname } = useLocation();
  const navigate = useNavigate();
  const is = (p) => pathname === p ? "text-green-700 font-semibold" : "text-gray-600";

  return (
    <header className="bg-white border-b sticky top-0 z-10">
      <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
        <button onClick={() => navigate(-1)} className="text-green-700 text-xl mr-2">‚Üê</button>
        <Link to="/dashboard" className="text-2xl font-bold text-green-700">PlantShazam</Link>
        <nav className="flex gap-4 text-sm">
          <Link className={is("/dashboard")} to="/dashboard">Dashboard</Link>
          <Link className={is("/search")} to="/search">Search</Link>
          <Link className={is("/about")} to="/about">About</Link>
        </nav>
      </div>
    </header>
  );
}
