import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function Header() {
  const { pathname } = useLocation();

  return (
    <header className="app-header">
      <div className="app-header-inner">
        {/* Brand (left) */}
        <Link to="/dashboard" className="brand">
          <span role="img" aria-label="sprout">ðŸŒ±</span>
          <span>PlantShazam</span>
        </Link>

        {/* Simple nav (right) */}
        <nav className="flex items-center gap-2">
          <Link
            to="/dashboard"
            className={`btn btn-ghost ${pathname === "/dashboard" ? "bg-green-100" : ""}`}
          >
            Dashboard
          </Link>
          <Link
            to="/search"
            className={`btn btn-ghost ${pathname === "/search" ? "bg-green-100" : ""}`}
          >
            Search
          </Link>
          <Link
            to="/about"
            className={`btn btn-ghost ${pathname === "/about" ? "bg-green-100" : ""}`}
          >
            About
          </Link>
        </nav>
      </div>
    </header>
  );
}
