import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function Header() {
  const { pathname } = useLocation();
  const navigate = useNavigate();
  const is = (p) =>
    pathname === p ? "text-green-700 font-semibold" : "text-gray-600 hover:text-green-700";

  return (
    <header className="bg-white/90 backdrop-blur border-b sticky top-0 z-20">
      <div className="max-w-6xl mx-auto px-4 h-14 flex items-center justify-between">
        <button
          onClick={() => navigate(-1)}
          className="text-green-700 text-lg px-2 py-1 rounded hover:bg-green-50"
          aria-label="Back"
        >
          ‚Üê
        </button>

        <Link to="/dashboard" className="text-2xl font-bold text-green-700 tracking-tight">
          PlantShazam
        </Link>

        <nav className="flex items-center gap-4 text-sm">
          <Link className={is("/dashboard")} to="/dashboard">Dashboard</Link>
          <Link className={is("/search")} to="/search">Search</Link>
          <Link className={is("/about")} to="/about">About</Link>
        </nav>
      </div>
    </header>
  );
}
