import React, { useEffect, useState } from "react";
import { getPlantById } from "../api/plantAPI";
import { useParams, Link } from "react-router-dom";

export default function PlantPage() {
  const { id } = useParams();
  const [plant, setPlant] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => { fetchPlant(); }, [id]);

  async function fetchPlant() {
    try {
      setLoading(true);
      const res = await getPlantById(id);
      setPlant(res);
    } catch (err) {
      console.error("Load plant failed:", err);
      alert("Failed to load plant");
    } finally {
      setLoading(false);
    }
  }

  if (loading) return <div className="page"><p className="text-gray-500">Loading…</p></div>;
  if (!plant) return <div className="page"><p className="text-gray-500">Not found</p></div>;

  return (
    <div className="page pb-24">
      <div className="mb-4">
        <Link to="/dashboard" className="text-sm text-gray-600 hover:underline">
          ← Back to Dashboard
        </Link>
      </div>

      <div className="card">
        {plant.image_url && (
          <img
            src={plant.image_url}
            alt={plant.name || "plant"}
            className="w-full max-h-[360px] object-cover rounded-lg mb-4"
          />
        )}

        <h1 className="h1">{plant.name || "Plant"}</h1>
        {plant.scientific_name && (
          <p className="text-gray-500 italic">{plant.scientific_name}</p>
        )}

        <div className="mt-4 grid sm:grid-cols-2 gap-4 text-sm">
          {plant.plantType && <Info label="Type" value={plant.plantType} />}
          {plant.sunlight && <Info label="Sunlight" value={plant.sunlight} />}
          {plant.watering && <Info label="Watering" value={plant.watering} />}
          {plant.soil && <Info label="Soil" value={plant.soil} />}
          {plant.fertilizer && <Info label="Fertilizer" value={plant.fertilizer} />}
          {plant.seasonality && <Info label="Seasonality" value={plant.seasonality} />}
        </div>

        {plant.uses_notes && (
          <div className="mt-4">
            <h3 className="font-semibold mb-1">Uses / Notes</h3>
            <p className="text-gray-700 whitespace-pre-line">{plant.uses_notes}</p>
          </div>
        )}

        {plant.qr_code && (
          <div className="mt-4">
            <h3 className="font-semibold mb-1">QR Code</h3>
            <img
              src={plant.qr_code}
              alt="QR code"
              className="h-40 w-40 object-contain border rounded-lg"
            />
          </div>
        )}
      </div>
    </div>
  );
}

function Info({ label, value }) {
  return (
    <div className="p-3 rounded-xl bg-gray-50 border">
      <div className="text-gray-500">{label}</div>
      <div className="font-medium text-gray-800">{value}</div>
    </div>
  );
}
